css:
.welcome {
  display: flex;
  font-size: 1.25em;
  line-height: 1.4;
  color: #acada4;
  background-color: #353630;
  overflow-x: auto;
}
.welcome p {
  line-height: inherit;
}
.welcome a {
  color: #6494ed;
}
.welcome ul {
  padding-left: 2em;
}
.welcome-container {
  width: 100%;
  max-width: 580px;
  min-width: 300px;
  margin: auto;
  padding: 3em 2em;
}
.welcome-header {
  margin: 0 0 6em 0;
}
.welcome-logo {
  display: block;
  width: 100%;
  max-width: 280px;
  margin: 0 auto 2em auto;
  color: #ddddda;
}
.welcome-title {
  font-size: 1.4em;
  text-align: center;
  line-height: 1.3;
  margin: 1em 0;
}
.welcome-card {
  margin: 1em 0;
  border-radius: 6px;
  border: 1px solid #20201d;
  background-color: #3d3e38;
}
.welcome-summary {
  padding: 1em 1.5em;
  font-size: 1.1em;
  font-weight: 300;
  line-height: 1.2;
  cursor: pointer;
}
.welcome-summary:hover {
  color: #b6b6af;
}
.welcome-summary::-webkit-details-marker {
  display: none;
}
.welcome-summary:before {
  width: 20px;
  color: rgba(172, 173, 164, 0.6);
}
details[open] .welcome-summary:before {
  color: #ddddda;
}
details[open] .welcome-summary {
  color: #ddddda;
}
.welcome-highlight {
  color: #ddddda;
}
.welcome-detail {
  border-top: 1px solid #20201d;
  padding: 1.5em;
}
.welcome-detail .welcome-note {
  margin-bottom: 0;
}
.welcome-img {
  display: block;
  max-width: 100%;
  border-radius: 3px;
  margin: 0 0 1em 0;
  border: 1px solid #20201d;
}
.welcome-key {
  display: inline-block;
  padding: .3em .4em;
  font-size: .8em;
  line-height: 1;
  border-radius: 3px;
  color: #ddddda;
  border: 1px solid rgba(172, 173, 164, 0.6);
  background: rgba(0, 0, 0, 0.1);
}
.welcome-note {
  font-size: .86em;
  color: rgba(172, 173, 164, 0.6);
}
.welcome-metrics {
  margin-top: 2em;
}
.welcome-footer {
  text-align: center;
}
