/* nimble light */
/* nimble light */
.about {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  -webkit-user-select: none;
  cursor: default;
  overflow: auto;
  text-align: center;
  font-size: 1.25em;
  line-height: 1.4;
  padding: 4em;
  color: #3a413e;
  background-color: #c8d5cf;
}
.about button {
  cursor: default;
}
.about a:focus {
  color: inherit;
}
.about img,
.about a {
  -webkit-user-drag: none;
}
.about .group-start {
  margin-top: 4em;
}
.about .group-item {
  margin-top: 1.5em;
}
.about-container {
  width: 100%;
  max-width: 500px;
}
.about-atom-io:hover .about-logo {
  color: #40a977;
}
.about-logo {
  display: block;
  width: 100%;
  max-width: 280px;
  margin: 0 auto 1em auto;
  color: #102d21;
  transition: color 0.2s;
}
.about-version-container:hover {
  color: #5d6964;
}
.about-version-container:active {
  color: #83918b;
}
.about-version {
  margin-right: .5em;
  font-size: 1.25em;
  vertical-align: middle;
}
.about-header-release-notes {
  vertical-align: middle;
  margin-left: 1em;
}
.about-updates-box {
  display: flex;
  align-items: center;
  padding: 1rem;
  border: 1px solid #939f9a;
  border-radius: 8px;
  background-color: #c0e4d5;
}
.about-updates-status {
  flex: 1;
  margin-left: .5em;
  text-align: left;
}
.about-updates-item,
.about-default-update-message .about-updates-label {
  display: none;
}
.about-updates-item.is-shown,
.about-default-update-message .about-updates-label.is-shown {
  display: block;
}
.about-updates-label {
  color: rgba(58, 65, 62, 0.82);
}
.about-updates-label.is-strong {
  color: #3a413e;
}
.about-updates-version {
  margin: 0 .4em;
}
.about-updates-release-notes {
  margin: 0 1em 0 1.5em;
}
.about-auto-updates {
  margin-top: 1em;
}
.about-auto-updates input {
  margin-right: .5em;
}
.about-metrics {
  font-size: .9em;
  line-height: 1.3;
  text-align: left;
  color: rgba(58, 65, 62, 0.82);
}
.about-love .icon::before {
  position: relative;
  top: 1px;
  width: auto;
  margin-right: 0;
  font-size: 1.5em;
}
.about-love .icon-logo-github::before {
  top: 3px;
}
.about-credits {
  color: rgba(58, 65, 62, 0.82);
}
.about-release-notes {
  color: #3399cc;
}
