/* nimble light */
/* nimble light */
.welcome {
  display: flex;
  font-size: 1.25em;
  line-height: 1.4;
  color: #3a413e;
  background-color: #c8d5cf;
  overflow-x: auto;
}
.welcome p {
  line-height: inherit;
}
.welcome a {
  color: #5cadd6;
}
.welcome ul {
  padding-left: 2em;
}
.welcome-container {
  width: 100%;
  max-width: 580px;
  min-width: 300px;
  margin: auto;
  padding: 3em 2em;
}
.welcome-header {
  margin: 0 0 6em 0;
}
.welcome-logo {
  display: block;
  width: 100%;
  max-width: 280px;
  margin: 0 auto 2em auto;
  color: #102d21;
}
.welcome-title {
  font-size: 1.4em;
  text-align: center;
  line-height: 1.3;
  margin: 1em 0;
}
.welcome-card {
  margin: 1em 0;
  border-radius: 8px;
  border: 1px solid #939f9a;
  background-color: #d1dbd7;
}
.welcome-summary {
  padding: 1em 1.5em;
  font-size: 1.1em;
  font-weight: 300;
  line-height: 1.2;
  cursor: pointer;
}
.welcome-summary:hover {
  color: #434c48;
}
.welcome-summary::-webkit-details-marker {
  display: none;
}
.welcome-summary:before {
  width: 20px;
  color: rgba(58, 65, 62, 0.82);
}
details[open] .welcome-summary:before {
  color: #102d21;
}
details[open] .welcome-summary {
  color: #102d21;
}
.welcome-highlight {
  color: #102d21;
}
.welcome-detail {
  border-top: 1px solid #939f9a;
  padding: 1.5em;
}
.welcome-detail .welcome-note {
  margin-bottom: 0;
}
.welcome-img {
  display: block;
  max-width: 100%;
  border-radius: 3px;
  margin: 0 0 1em 0;
  border: 1px solid #939f9a;
}
.welcome-key {
  display: inline-block;
  padding: .3em .4em;
  font-size: .8em;
  line-height: 1;
  border-radius: 3px;
  color: #102d21;
  border: 1px solid rgba(58, 65, 62, 0.82);
  background: rgba(0, 0, 0, 0.1);
}
.welcome-note {
  font-size: .86em;
  color: rgba(58, 65, 62, 0.82);
}
.welcome-metrics {
  margin-top: 2em;
}
.welcome-footer {
  text-align: center;
}
