progress {
  -webkit-appearance: none;
  height: 8px;
  border-radius: 4px;
  background-color: #181f26;
  box-shadow: inset 0 0 0 1px #8fa3a3;
}
progress::-webkit-progress-bar {
  background-color: transparent;
}
progress::-webkit-progress-value {
  border-radius: 4px;
  background-color: #85c1e9;
}
progress:indeterminate {
  background-image: linear-gradient(-45deg, transparent 33%, #85c1e9 33%, #85c1e9 66%, transparent 66%);
  background-size: 24px 8px, 100% 100%, 100% 100%;
  -webkit-animation: progress-buffering 5s linear 6;
}
@-webkit-keyframes progress-buffering {
  100% {
    background-position: -100px 0px;
  }
}
