.tab-bar {
  background: #0c1014;
  width: 100%;
  height: 32px;
  padding: 0;
  display: table;
  table-layout: fixed;
  -webkit-transform: scale(1);
}
.tab-bar .tab {
  color: #8fa3a3;
  font-family: "Lato";
  font-size: 13px;
  font-weight: 400;
  text-align: center;
  color: #424c4f;
  background: #040607;
  display: table-cell;
  width: 100%;
  height: 32px;
  line-height: 32px;
  box-sizing: border-box;
  border-left: 1px solid transparent;
  border-right: 1px solid transparent;
  border-top: 1px solid transparent;
  border-bottom: none;
  padding-left: 20px !important;
  padding-right: 20px !important;
  position: relative;
  transition: all 200ms ease;
}
.tab-bar .tab:not(:first-child) {
  border-left: 1px solid #0c1014;
}
.tab-bar .tab .status-modified,
.tab-bar .tab .status-added,
.tab-bar .tab .status-ignored {
  transition: color 200ms ease, background 200ms ease;
}
.tab-bar .tab .status-modified:hover,
.tab-bar .tab .status-added:hover,
.tab-bar .tab .status-ignored:hover {
  color: #ffffff;
}
.tab-bar .tab .status-modified {
  color: #876e44;
}
.tab-bar .tab .status-added {
  color: #47735d;
}
.tab-bar .tab:hover {
  color: #ffffff;
  background: #1c242d;
}
.tab-bar .tab:hover .close-icon {
  display: block;
}
.tab-bar .tab.active,
.tab-bar .tab:hover.active {
  color: #ffffff;
  background: #0c1014;
  width: 100%;
  border-bottom: 1px solid transparent;
}
.tab-bar .tab.active .title,
.tab-bar .tab:hover.active .title {
  color: #ffffff;
  padding: 0;
}
.tab-bar .tab.active:first-child,
.tab-bar .tab:hover.active:first-child {
  border-left: 0;
}
.tab-bar .tab.active:last-child,
.tab-bar .tab:hover.active:last-child {
  border-right: 0;
}
.tab-bar .tab.active .status-modified,
.tab-bar .tab:hover.active .status-modified {
  color: #f8c471;
}
.tab-bar .tab.active .status-added,
.tab-bar .tab:hover.active .status-added {
  color: #7dcea0;
}
.tab-bar .tab.active .status-ignored,
.tab-bar .tab:hover.active .status-ignored {
  color: #424c4f;
}
.tab-bar .tab.active[data-type="MarkdownPreviewView"],
.tab-bar .tab.active[data-type="TextEditor"] {
  color: #99d1ce;
  background: #0c1014;
}
.tab-bar .tab.active[data-type="MarkdownPreviewView"] .close-icon:before,
.tab-bar .tab.active[data-type="TextEditor"] .close-icon:before {
  color: #ffffff;
}
.tab-bar .tab.active[data-type="MarkdownPreviewView"] .close-icon:hover,
.tab-bar .tab.active[data-type="TextEditor"] .close-icon:hover {
  background: #283739;
}
.tab-bar .tab.active[data-type="MarkdownPreviewView"] .close-icon:hover:before,
.tab-bar .tab.active[data-type="TextEditor"] .close-icon:hover:before {
  color: #ffffff;
}
.tab-bar .tab.modified:hover .close-icon:before {
  color: #f8c471;
}
.tab-bar .tab.modified:before {
  content: '';
  width: 6px;
  height: 6px;
  top: 14px;
  left: 10px;
  border-radius: 100%;
  position: absolute;
  display: inline-block;
  box-shadow: inset 0 1px 0 rgba(0, 0, 0, 0.12);
  background-color: #f8c471;
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  border-color: transparent;
}
.tab-bar .tab .close-icon {
  top: 9px;
  height: 16px;
  width: 16px;
  right: 4px;
  position: absolute;
  color: inherit;
  opacity: 0;
  border-radius: 3px;
  transition: opacity 0.2s ease-in-out, background-color 0.2s ease-in-out;
}
.tab-bar .tab .close-icon:before {
  color: #424c4f;
  font-size: 10px;
  position: absolute;
  top: 3px;
  left: 4px;
  width: 8px;
  height: 8px;
  transition: background-color 0.2s ease-in-out;
}
.tab-bar .tab .close-icon:hover {
  background: #040607;
}
.tab-bar .tab:hover .close-icon {
  opacity: 1;
}
.tab-bar ~ .item-views .editor {
  padding-top: 4px;
  padding-bottom: 4px;
}
.tab-bar ~ .item-views .editor:before {
  height: 8px;
  position: absolute;
  width: 100%;
  top: 2px;
  left: 0;
  z-index: 1;
  display: block;
  content: "";
  background: linear-gradient(0deg, transparent, #0c1014);
}
.tab-bar ~ .item-views .editor:after {
  height: 8px;
  position: absolute;
  width: 100%;
  bottom: 2px;
  left: 0;
  z-index: 1;
  display: block;
  content: "";
  background: linear-gradient(0deg, #0c1014, transparent);
}
