.tree-view .project-root.project-root:first-child {
  padding-top: 0;
}
.tree-view .project-root.project-root:before {
  height: 32px;
  background-clip: padding-box;
}
.tree-view .project-root.project-root > .header .name {
  color: #8fa3a3;
  font-family: "Lato";
  font-size: 13px;
  font-weight: 600;
  color: #ffffff;
  height: 33px;
  line-height: 32px;
}
.tree-view .project-root.project-root > .header:before {
  color: #ffffff;
  transition: all 200ms ease;
}
.tree-view .project-root.project-root .tree-view-git-status {
  opacity: .54;
  margin-left: 8px;
  font-size: 14px;
}
.tree-view .project-root.project-root .tree-view-git-status:before {
  font-family: 'Octicons Regular';
  src: url("octicons.woff") format("woff");
  font-weight: normal;
  font-style: normal;
  content: "\f020";
  margin-right: 3px;
}
.tree-view .project-root.project-root .tree-view-git-status:after {
  content: "";
}
.tree-view .directory.selected:before {
  background: transparent !important;
}
.tree-view .list-tree .list-tree {
  border-left: 1px solid #10151a;
  margin-left: 3px;
}
.tree-view .list-tree .list-item.file.entry {
  transition: 0.2s ease-in;
}
.tree-view .list-tree .list-item.file.entry.selected:before {
  background: #0c1014;
}
.tree-view .list-tree .list-item.file.entry:after {
  font-family: 'Octicons Regular';
  src: url("octicons.woff") format("woff");
  font-weight: normal;
  font-style: normal;
  opacity: .26;
  content: "•";
  position: relative;
  font-size: 7px;
  float: left;
  top: 1px;
  left: -15.5px;
  width: 6px;
  margin-right: -8px;
}
.tree-view .list-tree .list-item.file.entry[class^="status-"]:after,
.tree-view .list-tree .list-item.file.entry[class*="status-"]:after {
  opacity: .54;
}
