/* +----------------------------------------------------------------------------+
   + Atom Text Editor (:host)                                               [1] +
   +----------------------------------------------------------------------------+ */
.atom-text-editor,
:host {
  background-color: #f9f9fc;
  color: #454546;
  /* +----------------------------------------------------------------------------+
     + Atom Text Editor (:host) - Cursor                                    [1.0] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[1.0]-+ */
  /* +----------------------------------------------------------------------------+
     + Atom Text Editor (:host) - Gutter                                    [1.1] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[1.1]-+ */
  /* +----------------------------------------------------------------------------+
     + Atom Text Editor (:host) - Indent Guide                              [1.2] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[1.2]-+ */
  /* +----------------------------------------------------------------------------+
     + Atom Text Editor (:host) - Invisibles                                [1.3] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[1.3]-+ */
  /* +----------------------------------------------------------------------------+
     + Atom Text Editor (:host) - Line                                      [1.4] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[1.4]-+ */
  /* +----------------------------------------------------------------------------+
     + Atom Text Editor (:host) - Selection                                 [1.5] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[1.5]-+ */
  /* +----------------------------------------------------------------------------+
     + Atom Text Editor (:host) - Wrap Guide                                [1.6] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[1.6]-+ */
}
.atom-text-editor .cursor,
:host .cursor {
  color: #454546;
}
.atom-text-editor .gutter,
:host .gutter {
  background-color: #f2f2f7;
  color: #454546;
}
.atom-text-editor .gutter .line-number,
:host .gutter .line-number {
  text-align: left;
}
.atom-text-editor .gutter .line-number.cursor-line,
:host .gutter .line-number.cursor-line {
  background-color: #f2f2f7;
  color: #6fbad0;
}
.atom-text-editor .gutter .line-number.cursor-line-no-selection,
:host .gutter .line-number.cursor-line-no-selection {
  color: #6fbad0;
}
.atom-text-editor .gutter .line-number.git-line-removed:before,
:host .gutter .line-number.git-line-removed:before {
  bottom: -3px;
}
.atom-text-editor .gutter .line-number.git-line-removed:after,
:host .gutter .line-number.git-line-removed:after {
  content: "";
  position: absolute;
  left: 0px;
  bottom: 0px;
  width: 25px;
  border-bottom: 2px dotted rgba(223, 92, 90, 0.5);
  pointer-events: none;
}
.atom-text-editor .gutter .line-number.folded .icon-right,
:host .gutter .line-number.folded .icon-right {
  color: #71a7ca;
}
.atom-text-editor .gutter .line-number.folded .icon-right:hover,
:host .gutter .line-number.folded .icon-right:hover {
  color: #6fbad0;
}
.atom-text-editor .gutter .line-number .icon-right,
:host .gutter .line-number .icon-right {
  color: #454546;
}
.atom-text-editor .gutter .gutter .line-number.folded,
:host .gutter .gutter .line-number.folded,
.atom-text-editor .gutter .gutter .line-number:after,
:host .gutter .gutter .line-number:after,
.atom-text-editor .gutter .fold-marker:after,
:host .gutter .fold-marker:after {
  color: #454546;
}
.atom-text-editor .indent-guide,
:host .indent-guide {
  color: #c6c6c8;
}
.atom-text-editor .invisible,
:host .invisible,
.atom-text-editor .invisible-character,
:host .invisible-character {
  color: #c6c6c8;
}
.atom-text-editor .line.cursor-line,
:host .line.cursor-line {
  background: #f2f2f7;
}
.atom-text-editor .selection .region,
:host .selection .region {
  background-color: #71a7ca;
  color: #454546;
}
.atom-text-editor .wrap-guide,
:host .wrap-guide {
  background-color: #c6c6c8;
}
/* +------------------------------------------------------------------------[1]-+ */
/* +----------------------------------------------------------------------------+
   + Atom Text Editor (:shadow)                                             [2] +
   +----------------------------------------------------------------------------+ */
atom-text-editor,
atom-text-editor::shadow {
  /* +----------------------------------------------------------------------------+
     + Atom Text Editor (:shadow) - Gutter                                  [2.0] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[2.0]-+ */
  /* +----------------------------------------------------------------------------+
     + Atom Text Editor (:shadow) - Result Marker                           [2.1] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[2.1]-+ */
}
atom-text-editor .gutter.drop-shadow,
atom-text-editor::shadow .gutter.drop-shadow {
  -webkit-box-shadow: -2px 0 10px 2px #f2f2f7;
}
atom-text-editor .find-result .region,
atom-text-editor::shadow .find-result .region {
  border: 1px solid #6fbad0 !important;
}
atom-text-editor .current-result .region,
atom-text-editor::shadow .current-result .region {
  border: 1px solid #6fbad0 !important;
  background-color: transparent;
}
