/* +----------------------------------------------------------------------------+
   + Status                                                                 [1] +
   +----------------------------------------------------------------------------+ */
.status {
  font-weight: normal;
  color: #454546;
}
/* +----------------------------------------------------------------------------+
   + Status - Information Level                                           [1.0] +
   +----------------------------------------------------------------------------+ */
.status-added {
  color: #c5c961;
}
.status-ignored {
  font-weight: normal;
