Arctic Versioning Specification (ArcVer)
  (http://specs.arcticicestudio.com/arcver)
*/
/* +----------------------------------------------------------------------------+
   + Bar                                                                    [1] +
   +----------------------------------------------------------------------------+ */
progress {
  -webkit-appearance: none;
  height: 8px;
  border-radius: 2px;
  background-color: #e2e2ed;
  box-shadow: inset 0 0 0 1px #f9f9fc;
  background-image: linear-gradient(-45deg, transparent 33%, rgba(113, 167, 202, 0.2) 33%, rgba(113, 167, 202, 0.2) 66%, transparent 66%);
  background-size: 25px 8px, 100% 100%, 100% 100%;
  -webkit-animation: animate-stripes 5s linear infinite;
}
progress::-webkit-progress-bar {
  background-color: transparent;
}
progress::-webkit-progress-value {
  border-radius: 2px;
  background-color: #71a7ca;
}
progress[value] {
  background-image: none;
  -webkit-animation: none;
}
@-webkit-keyframes animate-stripes {
  100% {
    background-position: -100px 0px;
  }
}
/* +------------------------------------------------------------------------[1]-+ */
/* +----------------------------------------------------------------------------+
   + Spinner                                                                [2] +
   +----------------------------------------------------------------------------+ */
.loading-spinner-large {
  width: 64px;
  height: 64px;
  display: block;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: cover;
}
.loading-spinner-large.inline-block {
  display: inline-block;
}
.loading-spinner-medium {
  width: 50px;
  height: 50px;
  display: block;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: cover;
}
.loading-spinner-medium.inline-block {
  display: inline-block;
}
.loading-spinner-small {
  width: 32px;
  height: 32px;
  display: block;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: cover;
}
.loading-spinner-small.inline-block {
  display: inline-block;
}
.loading-spinner-tiny {
  width: 20px;
  height: 20px;
  display: block;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: cover;
}
.loading-spinner-tiny.inline-block {
  display: inline-block;
}
