   +----------------------------------------------------------------------------+ */
.styleguide {
  padding: 0;
  /* +----------------------------------------------------------------------------+
   + Styleguide - Example                                                 [1.0] +
   +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[1.0]-+ */
  /* +----------------------------------------------------------------------------+
   + Styleguide - List                                                    [1.1] +
   +----------------------------------------------------------------------------+ */
