/* +----------------------------------------------------------------------------+
   + Heading                                                                [1] +
   +----------------------------------------------------------------------------+ */
h1,
h2,
h3 {
  line-height: 1em;
  margin-bottom: 15px;
}
h1 {
  font-size: 2em;
}
h2 {
  font-size: 1.5em;
}
h3 {
  font-size: 1.2em;
}
/* +------------------------------------------------------------------------[1]-+ */
/* +----------------------------------------------------------------------------+
   + Paragraph                                                              [2] +
   +----------------------------------------------------------------------------+ */
p {
  line-height: 1.6;
  margin-bottom: 15px;
}
/* +------------------------------------------------------------------------[2]-+ */
/* +----------------------------------------------------------------------------+
   + Label                                                                  [3] +
   +----------------------------------------------------------------------------+ */
label {
  font-weight: normal;
}
/* +------------------------------------------------------------------------[3]-+ */
/* +----------------------------------------------------------------------------+
   + Preformatted                                                           [4] +
   +----------------------------------------------------------------------------+ */
pre {
  box-shadow: none;
  color: #454546;
  background: #ffffff;
  border-radius: 2px;
  border: none;
  margin: 0;
}
/* +------------------------------------------------------------------------[4]-+ */
/* +----------------------------------------------------------------------------+
   + Code                                                                   [5] +
   +----------------------------------------------------------------------------+ */
code {
  font-weight: normal;
  color: #404041;
  background: #ffffff;
  border-radius: 2px;
}
/* +------------------------------------------------------------------------[5]-+ */
/* +----------------------------------------------------------------------------+
   + Appearance                                                             [6] +
   +----------------------------------------------------------------------------+ */
.selected {
  font-weight: normal;
  color: #404041;
}
.text-subtle {
  font-weight: normal;
  color: rgba(69, 69, 70, 0.6);
}
/* +------------------------------------------------------------------------[6]-+ */
/* +----------------------------------------------------------------------------+
   + Sizing                                                                 [7] +
   +----------------------------------------------------------------------------+ */
.text-smaller {
  font-size: 0.9em;
}
/* +------------------------------------------------------------------------[7]-+ */
/* +----------------------------------------------------------------------------+
   + Highlighting                                                           [8] +
   +----------------------------------------------------------------------------+ */
.text-highlight {
  font-weight: normal;
  color: #404041;
}
.highlight {
  padding: 1px 4px;
  font-weight: bold;
  text-shadow: none;
  border-radius: 2px;
  color: #404041;
  background-color: #ffffff;
}
.highlight-info {
  padding: 1px 4px;
  font-weight: bold;
  text-shadow: none;
  border-radius: 2px;
  color: #404041;
  background-color: rgba(107, 168, 208, 0.5);
}
.highlight-warning {
  padding: 1px 4px;
  font-weight: bold;
  text-shadow: none;
  border-radius: 2px;
  color: #404041;
  background-color: rgba(255, 197, 104, 0.5);
}
.highlight-error {
  padding: 1px 4px;
  font-weight: bold;
  text-shadow: none;
  border-radius: 2px;
  color: #404041;
  background-color: rgba(229, 86, 84, 0.5);
}
.highlight-success {
  padding: 1px 4px;
  font-weight: bold;
  text-shadow: none;
  border-radius: 2px;
  color: #404041;
  background-color: rgba(203, 207, 91, 0.5);
}
/* +------------------------------------------------------------------------[8]-+ */
/* +----------------------------------------------------------------------------+
   + Information Level                                                      [9] +
   +----------------------------------------------------------------------------+ */
.text-error {
  color: #df5c5a;
}
.text-info {
  color: #71a7ca;
}
.text-info:hover {
  color: #71a7ca;
}
.text-warning {
  color: #ffc568;
}
.text-warning:hover {
  color: #ffc568;
}
.text-success {
  color: #c5c961;
}
.text-success:hover {
  color: #c5c961;
}
