.tooltip {
  white-space: nowrap;
  font-size: 1.15em;
  /* +----------------------------------------------------------------------------+
     + Tooltip - Inner                                                      [1.0] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[1.0]-+ */
  /* +----------------------------------------------------------------------------+
     + Tooltip - Keystroke                                                  [1.1] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[1.1]-+ */
  /* +----------------------------------------------------------------------------+
     + Tooltip - Decoration                                                 [1.2] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[1.2]-+ */
}
.tooltip.in {
  opacity: 1;
  transition: opacity 0.12s ease-out;
}
.tooltip .tooltip-inner {
  line-height: 1;
  padding: 0.75em 0.975em;
  border-radius: 2px;
  background-color: #71a7ca;
  color: #f2f2f7;
  white-space: nowrap;
  max-width: none;
}
.tooltip .keystroke {
  font-family: "Helvetica Neue", Arial, sans-serif;
  font-size: 1em;
  padding: .1em .4em;
  margin: 0 -0.525em 0 0.375em;
  border-radius: 2px;
  color: #f2f2f7;
  background: #71a7ca;
}
.tooltip.top .tooltip-arrow {
  border-top-color: #71a7ca;
}
.tooltip.top-left .tooltip-arrow {
  border-top-color: #71a7ca;
}
.tooltip.top-right .tooltip-arrow {
  border-top-color: #71a7ca;
}
.tooltip.right .tooltip-arrow {
  border-right-color: #71a7ca;
}
.tooltip.left .tooltip-arrow {
  border-left-color: #71a7ca;
}
.tooltip.bottom .tooltip-arrow {
  border-bottom-color: #71a7ca;
}
.tooltip.bottom-left .tooltip-arrow {
  border-bottom-color: #71a7ca;
}
.tooltip.bottom-right .tooltip-arrow {
  border-bottom-color: #71a7ca;
}
