  /* +----------------------------------------------------------------------------+
     + Tree View - Project Root                                             [1.0] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[1.0]-+ */
}
.tree-view .selected:before {
  background: #ffffff !important;
}
.tree-view:focus .selected:before {
