   + markdown-preview                                                       [3] +
   +----------------------------------------------------------------------------+ */
.markdown-preview {
  font-size: 1.25em;
  color: #454546;
  background-color: #f2f2f7;
  /* +----------------------------------------------------------------------------+
    + markdown-preview - Atom Text Editor (shadow)                         [3.0] +
    +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[3.0]-+ */
  /* +----------------------------------------------------------------------------+
     + markdown-preview - Link                                              [3.1] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[3.1]-+ */
  /* +----------------------------------------------------------------------------+
     + markdown-preview - Blockquote                                        [3.2] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[3.2]-+ */
  /* +----------------------------------------------------------------------------+
     + markdown-preview - Code                                              [3.3] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[3.3]-+ */
  /* +----------------------------------------------------------------------------+
     + markdown-preview - Heading                                           [3.4] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[3.4]-+ */
  /* +----------------------------------------------------------------------------+
     + markdown-preview - Separator                                         [3.5] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[3.5]-+ */
  /* +----------------------------------------------------------------------------+
     + markdown-preview - Tab                                               [3.6] +
     +----------------------------------------------------------------------------+ */
  /* +----------------------------------------------------------------------[3.6]-+ */
}
.markdown-preview atom-text-editor::shadow .line.cursor-line {
  background-color: transparent;
}
.markdown-preview a {
  color: #71a7ca;
}
.markdown-preview blockquote {
  color: #454546;
  border-left-color: rgba(69, 69, 70, 0.6);
}
.markdown-preview code {
  color: #71a7ca;
  border-color: #f9f9fc;
  background-color: #ffffff;
  background-clip: padding-box;
}
.markdown-preview h1,
.markdown-preview h2 {
  color: inherit;
  border-color: #f9f9fc;
}
.markdown-preview hr {
  background-color: #f9f9fc;
}
.markdown-preview .tab.active[data-type="MarkdownPreviewView"] {
  background-color: #f2f2f7;
}
.markdown-preview .tab.active[data-type="MarkdownPreviewView"]:after {
  border-bottom-color: #f2f2f7;
}
